<style lang="scss">
@import "@/assets/style/_vars.scss";

.squad-join {
  margin: 130px auto 0;
  width: 70%;

  &__header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 26px 0 11px;
    margin-bottom: 4px;

    height: 42px;

    background-color: rgba($black, .7);
  }

  &__title {
    font-weight: 600;
    font-size: 2.4rem;
    letter-spacing: 0.08em;
    text-transform: uppercase;
  }

  &__item {
    display: grid;
    align-items: center;
    grid-template-columns: auto 13%;

    width: 100%;
    height: 75px;

    background-color: rgba($black, $opacity);

    transition: background-color .4s $ease;

    &:hover {
      background-color: rgba(#fff, .8);

      .squad-join__icon {
        fill: $black;
      }

      .squad-join__text {
        color: $black;
      }

      .squad-join__left {
        border-right-color: rgba($black, 0.3);
      }
    }

    &--new-squad {
      background-color: rgba(#fff, .8);

      .squad-join__icon {
        fill: $black;
      }

      .squad-join__text {
        color: $black;
      }

      .squad-join__left {
        border-right: 1px solid rgba($black, 0.3);
      }
    }
  }

  &__left {
    display: flex;
    align-items: center;
    padding-left: 24px;

    height: 100%;

    border-right: 1px solid rgba(#fff, 0.3);

    transition: border-color .4s $ease;
  }

  &__players {
    padding: 0 26px;
  }

  &__icon {
    margin-right: 21px;

    width: 54px;
    height: 47px;

    fill: #fff;

    transition: fill .4s $ease;
  }

  &__text {
    font-weight: 600;
    font-size: 2.2rem;
    letter-spacing: 0.02em;
    color: rgba(#fff, .9);

    transition: color .4s $ease;
  }

  &__footer {
    position: absolute;
    bottom: 67px;

    p {
      font-weight: 600;
      font-size: 2rem;
      line-height: 31px;
      letter-spacing: 0.04em;
      color: rgba(#fff, .9);
    }
  }

  &__actions {
    display: flex;
    margin-top: 19px;
    gap: 6px;

    .btn {
      padding: 7px 105px;
    }
  }
}
